mapper.select:
SELECT MOVIEID, USERID, MOVIERATE
FROM   MOVIERATE
 WHERE  USERID =#{userid}

mapper.delete:
DELETE FROM MOVIERATE
WHERE   MOVIEID=#{movieid} AND
      USERID=#{userid}

mapper.update:
UPDATE MOVIERATE
SET	   MOVIERATE=#{movierate}		  
WHERE  MOVIEID=#{movieid} AND
		USERID=#{userid}
